const MeetTheTeamShowcase = () => {
  const [expanded, setExpanded] = useState(false);

  const handleCopyCode = () => {
    navigator.clipboard.writeText(meetTheTeamCode.replace(/\\n/g, '\n'));
  };

  const handleToggleExpand = () => {
    setExpanded(!expanded);
  };

  return (
    <ShowcaseContainer>
      <ShowcaseTitle>Meet the Team</ShowcaseTitle>
      <p>A responsive team grid section following NVIDIA's design system.</p>
      {/* ===== Meet the Team Section: Responsive team grid ===== */}
      <ExampleContainer>
        <MeetTheTeam />
      </ExampleContainer>
