import React from 'react';

const BorderSection = () => {
  return (
    <div className="nv-section">
      <div className="nv-container-custom">
        <div className="nv-h--medium mb-3">Separator Example</div>
        <p className="nv-text-large mb-5">This is a 1px #CCCCCC separator, following NVIDIA layout and spacing.</p>
        <div style={{ height: '1px', background: '#CCCCCC', width: '100%' }} />
      </div>
    </div>
  );
};

export default BorderSection;
