import React from 'react';
import styled from 'styled-components';

const MediaGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;
  margin-top: 2rem;
`;

const MediaItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const MediaImage = styled.div`
  width: 100%;
  height: 200px;
  background: #f5f5f5;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #666;
  font-size: 0.875rem;
`;

const MediaTitle = styled.h3`
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
  color: #333;
`;

const MediaDescription = styled.p`
  font-size: 1rem;
  line-height: 1.5;
  color: #666;
  margin: 0;
`;

const TripleMediaSection = ({ title, description, mediaItems }) => {
  return (
    <div className="nv-section">
      <div className="nv-container">
        <div className="nv-h--medium mb-3">
          {title}
        </div>
        <p className="nv-text-large mb-5">
          {description}
        </p>
        <MediaGrid>
          {mediaItems.map((item, index) => (
            <MediaItem key={index}>
              <MediaImage>
                {item.image ? (
                  <img src={item.image} alt={item.title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                ) : (
                  'Placeholder Image'
                )}
              </MediaImage>
              <MediaTitle>{item.title}</MediaTitle>
              <MediaDescription>{item.description}</MediaDescription>
            </MediaItem>
          ))}
        </MediaGrid>
      </div>
    </div>
  );
};

export default TripleMediaSection;
