import React from 'react';

const mediaTeasers = [
  {
    title: 'Feature Title Style as Desktop Heading Smaller',
    image: 'https://via.placeholder.com/300x180?text=Media+1',
    links: [
      { text: 'Project', href: '#' },
      { text: 'Paper', href: '#' },
      { text: 'Code', href: '#' },
    ],
  },
  {
    title: 'Feature Title Style as Desktop Heading Smaller',
    image: 'https://via.placeholder.com/300x180?text=Media+2',
    links: [
      { text: 'Project', href: '#' },
      { text: 'Paper', href: '#' },
    ],
  },
  {
    title: 'Feature Title Style as Desktop Heading Smaller',
    image: 'https://via.placeholder.com/300x180?text=Media+3',
    links: [
      { text: 'Project', href: '#' },
      { text: 'Paper', href: '#' },
      { text: 'Code', href: '#' },
    ],
  },
];

const ThreeMediaTeaser = () => (
  <div className="nv-section">
    <div className="nv-container">
      <h2 className="nv-h--medium" style={{ textAlign: 'center', marginBottom: '2rem' }}>
        Projects and Publications
      </h2>
      <div className="nv-feature-teasers-grid">
        {mediaTeasers.map((teaser, idx) => (
          <div className="nv-teaser" key={idx}>
            <div className="nv-teaser-media">
              <img alt="Media" src={teaser.image} />
            </div>
            <div className="nv-teaser-content">
              <h2 className="nv-h--small" style={{ marginBottom: 8 }}>{teaser.title}</h2>
              <ul className="nv-list">
                {teaser.links.map((link, i) => (
                  <li key={i}>
                    <a href={link.href}>{link.text}</a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  </div>
);

export default ThreeMediaTeaser;
