import React from 'react';

const Card = ({ title, description, imageUrl, ...props }) => {
  return (
    <div className="nvidia-card" {...props}>
      {imageUrl && (
        <div className="nvidia-card__image">
          <img src={imageUrl} alt={title} />
        </div>
      )}
      <div className="nvidia-card__content">
        <h3 className="nvidia-card__title">{title}</h3>
        <p className="nvidia-card__description">{description}</p>
      </div>
    </div>
  );
};

export default Card;
