import React from 'react';

const mediaTeasers = [
  {
    title: 'Feature Title Style as Desktop Heading Smaller',
    image: 'https://s3.amazonaws.com/cms.ipressroom.com/219/files/20252/nvidia-cosmos.jpg',
    ctas: [
      { text: 'Project', href: '#' },
      { text: 'Paper', href: '#' },
      { text: 'Code', href: '#' },
    ],
  },
  {
    title: 'Feature Title Style as Desktop Heading Smaller',
    image: 'https://s3.amazonaws.com/cms.ipressroom.com/219/files/20252/nvidia-cosmos.jpg',
    ctas: [
      { text: 'Project', href: '#' },
      { text: 'Paper', href: '#' },
    ],
  },
  {
    title: 'Feature Title Style as Desktop Heading Smaller',
    image: 'https://s3.amazonaws.com/cms.ipressroom.com/219/files/20252/nvidia-cosmos.jpg',
    ctas: [
      { text: 'Project', href: '#' },
      { text: 'Paper', href: '#' },
      { text: 'Code', href: '#' },
    ],
  },
];

const ThreeMediaTeaser = () => (
  <div className="nv-section">
    <div className="nv-container">
      <h2 className="nv-h--medium" style={{ textAlign: 'center', marginBottom: '2rem' }}>
        Projects and Publications
      </h2>
      <div className="nv-feature-teasers-grid">
        {mediaTeasers.map((teaser, idx) => (
          <div className="nv-teaser nv-teaser-card" key={idx}>
            <div className="nv-teaser-media">
              <img alt="Media" src={teaser.image} />
            </div>
            <div className="nv-teaser-content">
              <div className="nv-teaser-header">
                <h2 className="nv-h--smallest">{teaser.title}</h2>
              </div>
              <div className="nv-teaser-footer" style={{ display: 'flex', alignItems: 'center', gap: 0 }}>
                {teaser.ctas.map((cta, i) => (
                  <React.Fragment key={i}>
                    <button className="nv-cta-secondary--small">{cta.text}</button>
                    {i < teaser.ctas.length - 1 && (
                      <span style={{ display: 'inline-block', width: 1, height: 24, background: '#cccccc', margin: '0 0.5rem' }} />
                    )}
                  </React.Fragment>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  </div>
);

export default ThreeMediaTeaser;
