import React from 'react';

const TripleMediaSection = ({ title, description, mediaItems }) => {
  return (
    <div className="nv-section">
      <div className="nv-container">
        <div className="nv-h--medium mb-3">
          {title}
        </div>
        <p className="nv-text-large mb-5">
          {description}
        </p>
        <div className="nv-grid nv-grid--3-cols">
          {mediaItems.map((item, index) => (
            <div key={index} className="nv-grid__item">
              <div className="nv-media-card">
                {item.image && (
                  <div className="nv-media-card__image">
                    <img src={item.image} alt={item.title} />
                  </div>
                )}
                <div className="nv-media-card__content">
                  <h3 className="nv-h--small mb-2">{item.title}</h3>
                  <p className="nv-text-small">{item.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TripleMediaSection;
